import React, { Component, Fragment } from 'react';

import Button from '../../components/Button'

import axios from 'axios';

class Products extends Component {

    constructor(props) {
        super(props);

        this.state = {
            id: props.match.params.id,
            loading: true,
            data: {},
        }
        console.log(props.match.params.id);
    }

    componentDidMount() {
        axios.get(`https://api.mercadolibre.com/items/${this.state.id}`)
            .then(({ data }) => {
                this.setState({
                    data,
                    loading: false,
                });
            })
    }

    renderContent() {
        const { data } = this.state;
        console.log(data)

        return (
            <div>
                <Fragment>

                    <div className="mdl-grid mdl-shadow--6dp">

                        <h2><img src={data.pictures[0].url} /></h2>
                        <div className="mdl-cell mdl-cell--6-col">
                            <span className="mdl-layout-title">{data.title}</span>
                            <div className="mdl-cell">Pre√ßo: {data.base_price}</div>
                            <div className="mdl-cell"><Button label="Comprar" /></div>
                        </div>

                    </div>
                </Fragment>
            </div >

        )

    }

    render() {
        const { loading } = this.state;

        return loading ?
            <div>Carregando...</div>
            : this.renderContent();
    }
}

export default Products
