import React, { Component, Fragment } from 'react';

import Button from '../../components/Button'

import axios from 'axios';

class Products extends Component {

    constructor(props) {
        super(props);

        this.state = {
            id: props.match.params.id,
            loading: true,
            data: {},
        }
        console.log(props.match.params.id);
    }

    componentDidMount() {
        axios.all([
            axios.get(`https://api.mercadolibre.com/items/${this.state.id}`)
        ])

            .then(({ data }) => {
                this.setState({
                    data,
                    loading: false,
                });
            })
    }

    renderContent() {
        const { data } = this.state;
        console.log(data)

        return (
            <div>
                <Fragment>

                    <div className="mdl-grid mdl-shadow--6dp">

                        <h1><img src={data.pictures[0].url} /></h1>
                        <div className="mdl-cell mdl-cell--6-col">
                            <div className="mdl-layout-title">{data.title}</div>
                            Preço: {data.base_price}
                            <div className="mdl-cell"><Button label="Comprar" /></div>
                            <p>Iphone Se 16gb Original Vitrine + Nota Fiscal PELÍCULA DE VIDRO DE BRINDE !!!!!!!!!!!!!!!!!!!!!! ********** FOTOS REAIS DO PRODUTO INFORMAÇÕES SOBRE O PRODUTO PRODUTO ACOMPANHA , FONTE + CABO ORIGINAL IMPORTADO FONE SIMILAR POIS NÃO CONSEGUIMOS COMPRAR ORIGINAL 100% , PORÉM PRODUTO DE QUALIDADE NÃO ACOMPANHA CAIXA ** SÃO TODOS SEVERAMENTE TESTADOS ANTES DO ENVIO , *** PRODUTO SEM MARCAS DE USO , SÃO TELEFONES QUE JA PASSARAM POR ATIVAÇÃO . EXCELENTE QUALIDADE E ÓTIMO PREÇO DE PROMOÇÃO COM GARANTIA DE 3 MESES CONTRA DEFEITOS DE FABRICAÇÃO, A GARANTIA COBRE DEFEITOS DE FABRICAÇÃO, QUE SE APRESENTADOS SERÁ REPARADO E ENVIADO DE VOLTA AO CLIENTE , ASSIM COMO QUALQUER OUTRA GARANTIA . ENVIO IMEDIATO , PRONTA ENTREGA EMPRESA SERIA , HONESTA E VERDADEIRA COM OS CLIENTES PRODUTO DE QUALIDADE</p>
                        </div>

                    </div>
                </Fragment>
            </div >

        )

    }

    render() {
        const { loading } = this.state;

        return loading ?
            <div id="p2" className="mdl-progress mdl-js-progress mdl-progress__indeterminate"></div>
            : this.renderContent();
    }
}

export default Products
